package Aula01;

public class Exemplo03 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a,b;
		a = 10;
		b = -10;
		boolean c,d;
		c = true;
		d = false;
		
		
		System.out.println(~a); // "~a" inverte o valor e subtrai 1
		System.out.println(~b); // "~b" inverte o valor e subtrai 1
		System.out.println(!c); // "!c" nega o valor
		System.out.println(!d); // "!d" nega o valor
		

	}

}
